import React, {useState} from 'react'
import TodoForm from './TodoForm'
import TodoList from './TodoList';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import EditIcon from '@mui/icons-material/Edit';

function TodoEl({todos, completeTodo, deleteTodo}) {

const [edit, setEdit] = useState({
    id: null,
    text: ''
});

const submitEdit = value => {
    editTodo(edit.id, edit.text);
    setEdit(todos);
}

  return todos.map((todo, index) => (
    <div className={todo.isComplete ? 'todo-complete' : 'todo-incomplete'}
    key={todo.index}>
        <div key={todo.id} onClick={() => completeTodo(todo.id)}>
            {todo.text}
        </div>
        <div className='material ui icons'>
            <DeleteForeverIcon 
            onClick={() => deleteTodo(todo.id)}/>
            <EditIcon 
            onClick={() => setEdit({id: todo.id, text: todo.text})}/>
        </div>
    </div>
  )) 
}

export default TodoEl
