import React, {useState, useEffect, useRef} from 'react';
import './TodoForm.css';

var id = 1;

function TodoForm(props) {

    const [input, setInput] = useState(props.editTodo ? props.edit.value : '');

    const focus = useRef(null);

    useEffect(() => {
        focus.current.focus();
    })

    const handleChange = e => {
        setInput(e.target.value);
    };

    const handleSubmit = e => {
        e.preventDefault();
        props.onSubmit({
            id: id++,
            text: input,
            completed: false
        });

        setInput('');
    };

    return (
    <form onSubmit={handleSubmit} className='create-todo'>
        <input
        type = "text"
        placeholder = "Add to do"
        name = "text"
        value = {input}
        className = ''
        onChange={handleChange}
        ref={focus}
        />
        <button className=''>
            Add Todo
        </button>
    </form>
  )
}

export default TodoForm;
