import React, {useState, useContext} from 'react'
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import EditIcon from '@mui/icons-material/Edit';
import './TodoEl.css';
import { Context, useGlobalState } from '../Store';
import TodoList from './TodoList';

function TodoEl(id, title, completed) {

  const [todoArray] = useGlobalState();
  console.log(todoArray.todos);

const deleteTodo = (id) =>
{
  const updatedArray = ((todoArray) => todoArray.todos.filter((todo) => todo.id !== id));
    Context.set({todos: updatedArray});
};

const [check, setCheck] = useState(completed);

  return (
  <div>
    <li>
        <label>
            <input
            type = "checkbox"
            checked={check}
            onChange={() => setCheck(!check)} />
            <span>{title}</span>
        </label>
        <EditIcon />
        <DeleteForeverIcon onClick={deleteTodo(0)}/>
    </li>
  </div>
  )
}

export default TodoEl;
