import React, {useState, useEffect, useCallback} from 'react'
import AllTodos from './AllTodos';
import './TodoList.css'

function TodoList() {

const initialGlobalState = {
    count: 0
  };

// Create a Context for the (global) State
const GlobalState = React.createContext();

const GlobalStateProvider = function ({ children }) {
  const [state, setState] = useState(initialGlobalState ?? {});

  const setGlobalState = useCallback(
    (data = {}) => {
      const newState = { ...state };

      // Loop over the data items by key, only updating those which have changed
      Object.keys(data).forEach((key) => {
        newState[key] = data[key];
      });

      // Update the state with the new State
      setState(newState);
    },
    [state, setState]
  );

  useEffect(() => {
    GlobalState.set = setGlobalState;
  }, [setGlobalState]);

  return (
    // Pass the current value of GlobalState, based on this components' State, down
    <GlobalState.Provider value={state}>{children}</GlobalState.Provider>
  );
};

const [todos, setTodos] = useState([]);
const [title, setTitle] = useState([]);
var id = 0;

const addTodo = () => {

    // if (title || /^\s*$/.test(title))
    // {
    //     return;
    // }

    setTodos([
        ...todos,
        {
            id: id++,
            title: title,
            completed: false
        }
    ])
    setTitle(''); 
}

const deleteTodo = id => {
    const removeArray = [...todos].filter(todo => todo.id !== id)
    setTodos(removeArray);
}

const editTodo = (id, updatedValue) => {
    if (!updatedValue.text || /^\s*$/.test(updatedValue.text))
    {
        return;
    }

    setTodos(prev => prev.map(item => (item.id === id ? updatedValue : item)));
}

  return (
    <div className='todo-list'>
        <div className='header'>TodoApp</div>
        <input
        type='text'
        value={title}
        onChange={event => setTitle(event.target.value)}
        />
        <button onClick={() => addTodo()}>
            Add Todo
        </button>
        <AllTodos todos={todos} />
    </div>
  )
}

export default TodoList;
