import React, {useState} from 'react'
import TodoEl from './TodoEl';
import TodoForm from './TodoForm'
import './TodoList.css'

function TodoList() {

const [todos, setTodos] = useState([]);
const [todoTitle, setTodoTitle] = useState('');
var id = 0;

const addTodo = todo => {
if (todo.key === 'Enter')
{
    if (!todo.text || /^\s*$/.test(todo.text))
    {
        return;
    }
    setTodos([...todos,
    {
        id: id++,
        title: todoTitle,
        completed: false 
    }])
    setTodoTitle('');
}
}

const deleteTodo = id => {
    const removeArray = [...todos].filter(todo => todo.id !== id)
    setTodos(removeArray);
}

const editTodo = (id, updatedValue) => {
    if (!updatedValue.text || /^\s*$/.test(updatedValue.text))
    {
        return;
    }

    setTodos(prev => prev.map(item => (item.id === id ? updatedValue : item)));
}


  return (
    <div className='todo-list'>
        <div className='header'>Write your todos below</div>
        <TodoForm onSubmit={addTodo} />
        <TodoEl 
        todos={todos}
        completeToDo={completed}
        deleteTodo={deleteTodo}
        editTodo={editTodo}
        />
    </div>
  )
}

export default TodoList;
